import React, {useState}  from "react";
import {Outlet} from "react-router-dom";
import {Home, ListAlt, ExitToApp, HowToReg, Event, AccountCircle} from '@material-ui/icons/';
import { isLogedIn } from "../App";

export function Navbar(props){
    let menu, menu2;
    if(!isLogedIn()){
        menu = <a class={props.page==="login"? "active":""} href="#"><ExitToApp />Log In</a>;
        menu2 = <a class={props.page==="signup"? "active":""} href="#"><HowToReg />Sign Up</a>
    }else{
        menu = <a class={props.page==="booking"? "active":""} href="#"><Event />Booking</a>
        menu2 = <a class={props.page==="profile"? "active":""} href="#"><AccountCircle />Profile</a>
    }
    return (
        <body>
            <main>
                <header>
                    <div class={props.page==="homepage"? "topnav homepage":"topnav"}>
                        <a class={props.page==="homepage"? "active":""} href="/"><Home />Home</a>
                        <a class={props.page==="menu"? "active":""} href="#"><ListAlt />Menu</a>
                        {menu}
                        {menu2}
                    </div>

                    {props.page==="homepage" &&
                        <div>
                            {restaurantName()}
                            <div class="center booking">
                                <a href="#">Book a Table</a>
                            </div>
                        </div>
                    }
                </header>
                <Outlet />
            </main>
        </body>
    );
}

function restaurantName(){
    return(
        <div class="title">
            <h1 class="ml5">
                <span class="text-wrapper">
                    <span class="line line1"></span>
                    <span class="letters letters-left">Le Bistrot</span>
                    <span class="letters letters-right">d'Andre</span>
                    <span class="line line2"></span>
                </span>
            </h1>
        </div>
    );
}

//https://cdnjs.cloudflare.com/ajax/libs/animejs/2.0.2/anime.min.js
function animation(){
    const [data, setData] = useState(null);
    useEffect(() => {
        fetch(`https://cdnjs.cloudflare.com/ajax/libs/animejs/2.0.2/anime.min.js`)
          .then((response) => response.json())
          .then(setData)
      }, []);
    if(!data) return null;

    anime.timeline({loop: true})
    .add({
        targets: '.ml5 .line',
        opacity: [0.5,1],
        scaleX: [0, 1],
        easing: "easeInOutExpo",
        duration: 1000
    }).add({
        targets: '.ml5 .line',
        duration: 1000,
        easing: "easeOutExpo",
        translateY: (el, i) => (-0.625 + 0.625*2*i) + "em"
    }).add({
        targets: '.ml5 .letters-left',
        opacity: [0,1],
        translateX: ["0.5em", 0],
        easing: "easeOutExpo",
        duration: 1500,
        offset: '-=300'
    }).add({
        targets: '.ml5 .letters-right',
        opacity: [0,1],
        translateX: ["-0.5em", 0],
        easing: "easeOutExpo",
        duration: 1500,
        offset: '-=600'
    }).add({
        targets: '.ml5',
        opacity: 0,
        duration: 2000,
        easing: "easeOutExpo",
        delay: 1000
    });
}
