import React, {useEffect, useState} from "react";
import {logIn} from "../App.js";

export function Login(){
    const [username, setUsername] = useState(null);
    useEffect(() => {}, [username]);

    const [password, setPassword] = useState(null);
    useEffect(() => {}, [password]);

    const [email, setEmail] = useState(null);
    useEffect(() => {}, [email]);

    const [data, setData] = useState(null);
    useEffect(() => {}, []);

    function signIn(){
        if(!(username && password)){
            return alert("Invalid username/email and/or password!");
        }

        fetch(`/api/login?username=${username}&email=${email}&password=${password}`)
            .then((res) => res.json())
            .then((data) => setData(data));

        if(data.status==="error"){
            alert(data.message);
        }else{
            // logIn();
        }
    }

    return(
        <section className="form-signin">
            {title()}
            {dataFields()}            
            {rememberMe()}
            <button className="w-100 btn btn-lg btn-primary" onClick={login}>Login</button>
            {signupLink()}
        </section>
    );
}
function title(){
    return(
        <section>
            <h1>Welcome to</h1>
            <h3>Le Bistrot d'Andre</h3>
        </section>
    )
}
function dataFields(){
    return(
        <section>
            <div className="form-floating">
                <input type="text" className="form-control" id="floatingInput" placeholder="username"/>
                <label for="floatingInput">Email Address/Username</label>
            </div>
            <div className="form-floating">
                <input type="password" className="form-control" id="floatingPassword" placeholder="Password"/>
            <   label for="floatingPassword">Password</label>
            </div>
        </section>
    )
}
function rememberMe(){
    return (
        <div className="checkbox mb-3">
            <label>
                <input type="checkbox" value="remember-me"/> Remember me
            </label>
        </div>
    );
}
function signupLink(){
    return(
        <a href="/signup">Create new account</a>
    );

}

function login(){
    logIn();
    window.location.href='/';
}
