import React, {useState, useEffect} from "react";
import '../style/menu.css';

export function Menu(){
    // setMenu();

    const [menuItems, setMenuItems] = useState(null);
    useEffect(() => {
        fetch(`/api/getallmeals`)
            .then((res) => { return res.json(); } )
            .then((data) => {
                setMenuItems(JSON.stringify(data.menuItem));
            });
    }, []);

    return(
        <section>
            {/* <p>{JSON.parse(menuItems).name}</p> */}
            {/* {test(menuItems)} */}
            {menuItem()}
        </section>
    );
}

function test(menuItems){
    let items = JSON.parse(menuItems);
    return(
        <p>{menuItems}</p>
    )
}

//this function is just there to show you what design would looks like. Not the final code!
function menuItem(){
    return(
        <section className="menu-list">
            <div className="menu-item">
                <img src="https://uppercutsteakhouse.com/wp-content/uploads/2019/09/menu-dry-agedc.jpg" alt="Menu 1, Description, Price"/>
                <div className="name">Menu 1</div>
                <div className="description">Description</div>
                <div className="price">$xxx</div>
            </div>
            <div className="menu-item">
                <img src="https://i.pinimg.com/564x/77/c5/e7/77c5e7acb0f6e244bfeed141f19b8b71.jpg" alt="Menu 2, Description, Price"/>
                <div className="name">Menu 2</div>
                <div className="description">Description</div>
                <div className="price">$xxx</div>
            </div>
            <div className="menu-item">
                <img src="https://i.pinimg.com/564x/d4/c9/31/d4c9317f618ef2eff5e74cd91240460b.jpg" alt="Menu 3, Description, Price"/>
                <div className="name">Menu 3</div>
                <div className="description">Description</div>
                <div className="price">$xxx</div>
            </div>
            <div className="menu-item">
                <img src="https://i.pinimg.com/564x/6c/6e/8d/6c6e8dabd1808b9b17c0f0425e812f60.jpg" alt="Menu 4, Description, Price"/>
                <div className="name">Menu 4</div>
                <div className="description">Description</div>
                <div className="price">$xxx</div>
            </div>
            <div className="menu-item">
                <img src="https://i.pinimg.com/474x/96/44/81/964481b56cc24fb0b5cb26163d8c7d1e.jpg" alt="Menu 5, Description, Price"/>
                <div className="name">Menu 5</div>
                <div className="description">Description</div>
                <div className="price">$xxx</div>
            </div>
        </section>
    )
}
function mealItem(image, name, description, price){
    return(
        <div className="menu-item">
            <img src={image} alt={name+" "+description+" "+" "+price}/>
            <div className="name">{name}</div>
            <div className="description">{description}</div>
            <div className="price">{"$"+price}</div>
        </div>
    );
}

// This function is just for demonstration purpose only. In real-life, it will not be implemented and the manager will need to add the meal items by themselve.
function deleteMealItem(){
    fetch(`/api/deleteallmeal`)
        .then((res) => { return res.json(); } )
        .then((data) => {});
}
function setMenu(){
    deleteMealItem();
    const images = [
        "https://uppercutsteakhouse.com/wp-content/uploads/2019/09/menu-dry-agedc.jpg", "https://i.pinimg.com/564x/77/c5/e7/77c5e7acb0f6e244bfeed141f19b8b71.jpg",
        "https://i.pinimg.com/564x/d4/c9/31/d4c9317f618ef2eff5e74cd91240460b.jpg", "https://i.pinimg.com/564x/6c/6e/8d/6c6e8dabd1808b9b17c0f0425e812f60.jpg", "https://i.pinimg.com/474x/96/44/81/964481b56cc24fb0b5cb26163d8c7d1e.jpg"
    ];
    const names = ["Meal 1", "Meal 2", "Meal 3", "Meal 4", "Meal 5"];
    const descriptions = ["Description 1", "Description 2", "Description 3", "Description 4", "Description 5"];
    const prices = [25, 25, 20, 27, 23];
    const costs = [8, 8, 6, 9, 7];
    const types = ["all", "all", "all", "all", "all"];

    for(let i=0; i<5; ++i){
        addMealItem(images[i], names[i], descriptions[i], prices[i], costs[i], types[i]);
    }
}
function addMealItem(img, nm, des, prc, cst, typ){
    const data = {
        image: img,
        name: nm,
        description: des,
        price: prc,
        cost: cst,
        type: typ
    }
    fetch('/api/addmeal', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
        })
        .then((res) => { return res.json(); } )
        .then((data) => {});
}
