import React from "react";
import '../style/profile.css';
import { isLoggedIn } from "../App";

export function Profile(){
    const [username, setUsername] = React.useState(null);
    React.useEffect(() => {
        fetch(`/api/profile?userID=${isLoggedIn()}`)
            .then((res) => {return res.json()})
            .then((data) => setUsername(data.username));
    }, []);
    const [email, setEmail] = React.useState(null);
    React.useEffect(() => {
        fetch(`/api/profile?userID=${isLoggedIn()}`)
            .then((res) => {return res.json()})
            .then((data) => setEmail(data.email));
    }, []);
    const [firstName, setFirstName] = React.useState(null);
    React.useEffect(() => {
        fetch(`/api/profile?userID=${isLoggedIn()}`)
            .then((res) => {return res.json()})
            .then((data) => setFirstName(data.firstName));
    }, []);
    const [lastName, setLastName] = React.useState(null);
    React.useEffect(() => {
        fetch(`/api/profile?userID=${isLoggedIn()}`)
            .then((res) => {return res.json()})
            .then((data) => setLastName(data.lastName));
    }, []);

    const items = [];
    for(var i=0; i<6; ++i){
        items.push(personalInformation("Username", username));
    }

    return(
        <section className="profile">
            <div className="container">
                <div className="main-body">
                    <div className="row gutters-sm">
                        <div className="col-md-4 mb-3">
                            <div className="card">
                                <div className="card-body">
                                    <div className="d-flex flex-column align-items-center text-center">
                                        <img src="https://i.pinimg.com/564x/89/64/99/8964998576cfac440b3a14df748fc670.jpg" alt="Admin" className="rounded-circle" width="200" height="200"/>
                                        <div className="mt-3">
                                            <h4>{firstName + " " + lastName}</h4>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="col-md-8">
                            <div className="card mb-3">
                                <div className="card-body">

                                    {/* <div className="row">
                                        <div className="col-sm-3">
                                            <h6 className="mb-0">First Name</h6>
                                        </div>
                                        <div className="col-sm-9 text-secondary">{firstName}</div>
                                    </div>
                                    <hr/> */}
                                    {items}
                                    {/* <div className="row">
                                        <div className="col-sm-12">
                                            <a className="btn btn-info " target="__blank" href="#">Edit</a>
                                        </div>
                                    </div> */}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    );
}

function personalInformation(title, data){
    return(
        <secion>
            <div className="row">
                <div className="col-sm-3">
                    <h6 className="mb-0">{title}</h6>
                </div>
                <div className="col-sm-9 text-secondary">{data}</div>
            </div>
            <hr/>
        </secion>
    );
}
